let modal=document.getElementById("modal"),request=document.getElementsByClassName("request"),modalClose=document.getElementsByClassName("modal__close")[0];for(i=0;i<request.length;i++)request[i].onclick=function(){modal.style.display="block",document.body.style.overflowY="hidden",document.body.style.paddingRight="15px "};modalClose.onclick=function(){modal.style.display="none",document.body.style.overflowY="scroll",document.body.style.paddingRight="0px "},window.onclick=function(e){e.target==modal&&(modal.style.display="none",document.body.style.overflowY="scroll",document.body.style.paddingRight="0px ")};